// src/utils/compose-refs.ts
var isRefFn = (ref) => typeof ref === "function";
function setRef(ref, value) {
  if (isRefFn(ref)) {
    ref(value);
  }
}
function composeRefs(...refs) {
  return (node) => {
    for (const ref of refs) {
      setRef(ref, node);
    }
  };
}

export {
  composeRefs
};
